import React, {Fragment, useState} from 'react'
import {Link} from 'react-router-dom'
import './Navbar.css'

function Navbar() {

    // mobile section
    const [click,setClick] = useState(false)
    const handleClick = () => setClick(!click)
    const closeMobileMenu = () => setClick(false) // when menu option selected, get rid of side bar. 
    const [button, useButton] = useState(true)
    const showButton = () => {
        if(window.innerWidth <= 960){
            setButton (false)
        } else {
            setButton(true)
        }
    }

    // whenever the screen size changes, check for the code
    window.addEventListener()

    return (
        <>
        <nav className="navbar">
            <div className = "navbar-container">

                <Link to="/" className = "navbarLogo">
                    Some Kinda Nav <i className="fab fa-typo3"></i>
                </Link>

                {/* Mobile Menu Section  */}
                <div className="menu-icon" onClick={handleClick}>
                    <i className={click ? 'fas fa-times' : 'fas fa-bars'}/>
                </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                    <li className='nav-item'>
                        <Link to="/" className='nav-links' onClick={closeMobileMenu}>
                            home
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to="/About" className='nav-links' onClick={closeMobileMenu}>
                            About
                        </Link>
                    </li>
                    <li className='nav-item'>
                        <Link to="/Contact" className='nav-links-mobile' onClick={closeMobileMenu}>
                            Contact
                        </Link>
                    </li>
                </ul>
                {button && <Button buttonStyle = 'btn-outline'>Contact</Button>}

            </div>
        </nav>
        </>
    )
}

export default Navbar








