import React, { useState } from "react";
import './ContactUs.css'

function ContactUs () {


  const [status, setStatus] = useState("Submit");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, email, message } = e.target.elements;
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };

        return (
            <>
                    <div class = "shadow-lg" className="formContainer">
                        <h1>SEND US AN EMAIL PLEASE MOTHA FUKA</h1>

                                <form onSubmit={handleSubmit}>

                                <div className = 'formItem'>
                                        <label htmlFor="name">Name:</label>
                                        <input className='input' type="text" id="name" required />
                                </div>
                                <div className = 'formItem'>
                                        <label  htmlFor="email">Email:</label>
                                        <input className='input'  type="email" id="email" required />
                                </div>
                                <div className = 'formItem'>
                                        <label htmlFor="message">Short Message:</label>
                                        <textarea className ="textArea" id="message" required />
                                </div>
                                <button className = 'submitButton' type="submit">{status}</button>
                                </form>

                    </div>
            </>
        )
 }

export default ContactUs
