import React, { useState } from "react";
import './ContactUs.css'
import emailjs from 'emailjs-com';

function ContactUs () {


  const [status, setStatus] = useState("Submit");

  //setStatus("Sending...");

  function sendEmail(e) {
    e.preventDefault();

    emailjs.sendForm('service_xqey9j7', 'template_iaj6ueg', e.target, 'user_mDTAtWfAVpmZRWrpjPBaB')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
    alert('Thanks for getting in touch, our team will reach out to you');
  }




        return (
            <>
                    <div class = "shadow-lg" className="formContainer">
                        <h1>SEND US AN EMAIL PLEASE MOTHA FUKA</h1>

                                <form onSubmit={sendEmail}>

                                <div className = 'formItem'>
                                        <label htmlFor="name">Name:</label>
                                        <input className='input' type="text" id="name" required placeholder="Your message" />
                                </div>
                                <div className = 'formItem'>
                                        <label  htmlFor="email">Email:</label>
                                        <input className='input'  type="email" id="email" required />
                                </div>
                                <div className = 'formItem'>
                                        <label htmlFor="message">Short Message:</label>
                                        <textarea className ="textArea" id="message" required />
                                </div>
                                <button className = 'submitButton' type="submit">{status}</button>
                                </form>

                    </div>
            </>
        )
 }

export default ContactUs
