import '../../App.css';
import React from 'react';
import {Grid} from '@material-ui/core'
import {makeStyles} from '@material-ui/core/styles'


const useStyles = makeStyles((theme)=>({
  grid: {
      width: '100%',
      margin: '0px',
      height: '100%'

  }
}));

function About() {




  const classes = useStyles();

  return (
        <div>
            <Grid container spacing={10} className={classes.grid}>

                <Grid item xs={12} sm={6} md= {4}>
                    <div class="max-w-md mx-auto bg-white rounded-xl shadow-lg overflow-hidden md:max-w-2xl">
                        <div class="md:flex">
                            {/* <div class="md:flex-shrink-0">
                            <img class="w-full md:w-48" src={micklefieldhallLogo}  alt="Man looking at item at a store"/>
                            </div> */}
                            <div class="p-8">
                                <p class="text-colourTextGrey">"Very helpful and a pleasure to deal with"</p>
                                <div class="text-colourTextDark font-bold uppercase mt-6">- Jamie Rankin, CEO</div>
                                <div class="text-colourTextGrey">Micklefield Hall</div>
                            </div>
                        </div>
                    </div>
                </Grid>

            </Grid>
        </div>
  );


}

export default About;
